//MARK: - test
extension ViewController {
    
    struct widgetEntry: TimelineEntry {
        var date: Date
        var identifier: String
    }
    
    func setupUI() {
        let button = UIButton(frame: CGRectMake(10, 60, 200, 30))
        button.backgroundColor = .green
        button.setTitle("开始工作", for: .normal)
        button.addTarget(self, action: #selector(testBeginAction), for: .touchUpInside)
        view.addSubview(button)
    }
    
    @objc func testBeginAction() {
        print("begin ...")
        print(NSHomeDirectory())
        testGetData()
    }

    ///模拟网络请求，拿到字符串
    func testGetData() {
        // 单个widget配置json
        let sss = testGetWidgetViewJson()
        // 转widget模型
        let wmodel = WidgetModel.parseJSON(str: sss)
        // 根据模型展示View
        let wVc = UIHostingController(rootView: DynamicWidgetView(widgetEnvironment: .App, contents: (wmodel.contents)!, entry: widgetEntry(date: Date(), identifier: "widgetIdentifier")))
        let wview = wVc.view
        wview?.translatesAutoresizingMaskIntoConstraints = false
        self.addChild(wVc)
        view.addSubview(wview!)
        wVc.didMove(toParent: self)
        wview?.layer.cornerRadius = 30
        wview?.layer.masksToBounds = true
        
        var p = (155, 155)
        if wmodel.widgetType == .systemMedium {
//            p = (329, 155)
            p = (338, 158)
        }
        else if wmodel.widgetType == .systemLarge {
            p = (250, 262)
        }
        
        NSLayoutConstraint.activate([
            (wview?.widthAnchor.constraint(equalToConstant: CGFloat(p.0)))! as NSLayoutConstraint,
            (wview?.heightAnchor.constraint(equalToConstant: CGFloat(p.1)))! as NSLayoutConstraint,
            (wview?.leftAnchor.constraint(equalTo: view.leftAnchor, constant: 10))! as NSLayoutConstraint,
            (wview?.topAnchor.constraint(equalTo: view.topAnchor, constant: 100))! as NSLayoutConstraint
        ])
        
        updateDataBase(wmodel: wmodel, json: sss)
    }
        
    
    
