    func updateDataBase(wmodel: WidgetModel, json: String) {
        // 先更新数据库字段isCurrent
        if wmodel.widgetType?.rawValue == 3 {
            if let current = WidgetDataManager.shared.getCurrentSmallWidget() {
                current.isCurrent = false
                current.isCurrentShow = ""
                WidgetDataManager.shared.updateSmallWidget(model: current)
            }
        }
        else if wmodel.widgetType?.rawValue == 2 {
            if let current = WidgetDataManager.shared.getCurrentMediumWidget() {
                current.isCurrent = false
                current.isCurrentShow = ""
                WidgetDataManager.shared.updateMediumWidget(model: current)
            }
        }
        else if wmodel.widgetType?.rawValue == 1 {
            if let current = WidgetDataManager.shared.getCurrentLargeWidget() {
                current.isCurrent = false
                current.isCurrentShow = ""
                WidgetDataManager.shared.updateLargeWidget(model: current)
            }
        }
        
       // 保存新的widget到数据库
        var subTagID = ""
        let baseModel = WidgetDBModel()
        baseModel.categoryId = wmodel.categoryId
        baseModel.topTagId = wmodel.topTagId
        if let widgetID = wmodel.widgetId {
            let subCount = WidgetDataManager.shared.getSameWidgets(with: widgetID)
            subTagID = "\(widgetID)_\(subCount + 1)"
            baseModel.subTagID = subTagID
//            UserDefaults(suiteName: AppGroup.defaultGroup.rawValue)?.set(true, forKey: AppGroupConst.updateHomeWidget)
        }
        baseModel.navigationURL = wmodel.navigationURL
        baseModel.widgetId = wmodel.widgetId
        baseModel.widgetType = wmodel.widgetType?.rawValue
        baseModel.contents = json
        baseModel.isCurrent = true // 设置当前展示中
        baseModel.isCurrentShow = "展示中"
        WidgetDataManager.shared.insertWidgetModel(model: baseModel)

        if wmodel.widgetType?.rawValue == 3 {
            UserDefaults(suiteName: AppGroup.defaultGroup.rawValue)?.set(subTagID, forKey: AppGroupConst.currentSmallSubTag)
            WidgetCenter.shared.reloadTimelines(ofKind: "SmallWidget")
        } else if wmodel.widgetType?.rawValue == 2 {
            UserDefaults(suiteName: AppGroup.defaultGroup.rawValue)?.set(subTagID, forKey: AppGroupConst.currentMediumSubTag)
            WidgetCenter.shared.reloadTimelines(ofKind: "MediumWidget")
        } else if wmodel.widgetType?.rawValue == 1 {
            UserDefaults(suiteName: AppGroup.defaultGroup.rawValue)?.set(subTagID, forKey: AppGroupConst.currentLargeSubTag)
            WidgetCenter.shared.reloadTimelines(ofKind: "LargeWidget")
        }
    }
    
    
    
    
    
